import React, { useState, useEffect } from "react";
import Prism from "prismjs";
import marked from "marked";
import "./Okadia.css";

function App() {
  const [dataType, setDataType] = useState("");
  const [previewContent, setPreviewContent] = useState(
    ""
    // `import react from "react"
    //   123 eede
    // `
  );

  const handleChange = e => {
    // var x = document.getElementById("myInput").value;
    console.log(e);
    setPreviewContent(`${e.target.value}`);
    // setPreviewContent(marked(x));
  };
  const handleChangeTypeJS = () => {
    setDataType(`js`);
  };
  const handleChangeTypeCSS = () => {
    setDataType(`css`);
  };
  const handleChangeTypeC = () => {
    setDataType("c");
  };
  useEffect(() => {
    setTimeout(() => Prism.highlightAll(), 0);
  }, []);

  return (
    <div style={{ display: "flex" }}>
      {/* -----------------代码input区------------------ */}
      <div>
        <div>
          <ul>
            <button onClick={handleChangeTypeJS}>JavaScript</button>
            <button onClick={handleChangeTypeCSS}>CSS</button>
            <button onClick={handleChangeTypeC}>C++</button>
          </ul>
        </div>

        {/* <div
          type="input"
          id="myInput"
          style={{
            width: "500px",
            height: "100%",
            backgroundColor: "#f8f8f2"
          }}
          onInput={handleChange}
          dangerouslySetInnerHTML={{ __html: previewContent }}
        /> */}

        <input
          value={previewContent}
          placeholder="输入代码..."
          onChange={handleChange}
          style={{
            width: "500px",
            height: "100%",
            backgroundColor: "#f8f8f2"
          }}
        ></input>
      </div>
      {/* -----------------代码view区------------------ */}
      <div>
        <ul>代码区:{dataType}</ul>
        <pre className="line-numbers" style={{ width: 500, height: "100%" }}>
          <code className="language-js">
            {/* <div dangerouslySetInnerHTML={{ __html: previewContent }}></div> */}
            {/* {`${previewContent}`} */}
            {previewContent}
            {/* import react from "ddd" */}
          </code>
        </pre>
      </div>
    </div>
  );
}
export default App;
