import React, { useState, useEffect } from "react";

import "./styles.css";

function CodeEditor() {
  const [code, setCode] = useState("");

  const handleChange = e => {
    console.log(e);
    setCode(`${e.target.value}`);
  };

  useEffect(() => {
    Prism.highlightAll();
  });

  return <div></div>;
}

export default CodeEditor;
