import React, { useState, useEffect } from "react";
import Prism from "prismjs";
import { highlight, languages } from "prismjs/components/prism-core";
// import "../CodeTheme/Okadia.css";
import "./styles.css";

function CodeEditor() {
  const [code, setCode] = useState("");
  const padding = 20;
  const handleChange = e => {
    console.log(e);
    setCode(`${e.target.value}`);
  };

  useEffect(() => {
    Prism.highlightAll();
  });

  const highlighted = code => {
    highlight(code, languages.jsx);
  };
  return (
    <div>
      <div
        // className="container__content"
        style={{
          width: 1000,
          maxWidth: "100%",
          padding: 20,
          // textAlign: "center",
          backgroundColor: "#eee8aa",
          //---
          // color: #f8f8f2;
          // background: none;
          // text-shadow: 0 1px rgba(0, 0, 0, 0.3);
          // font-family: Consolas, Monaco, "Andale Mono", "Ubuntu Mono", monospace;
          // font-size: 1em;
          // text-align: left;
          // white-space: pre;
          // word-spacing: normal;
          // word-break: normal;
          // word-wrap: normal;
          // line-height: 1.5;
          //---
          position: "relative",
          textAlign: "left",
          boxSizing: "border-box",
          // padding: 0,
          overflow: "hidden",
          fontSize: 18,
          height: 500
        }}
      >
        <textarea
          value={code}
          type="text"
          onChange={handleChange}
          style={{
            margin: 0,
            border: 0,
            background: "none",
            //---------继承父元素------|
            boxSizing: "inherit",
            display: "inherit",
            fontFamily: "inherit",
            fontSize: "inherit",
            fontStyle: "inherit",
            fontVariantLigatures: "inherit",
            fontWeight: "inherit",
            letterSpacing: "inherit",
            lineHeight: "inherit",
            tabSize: "inherit",
            textIndent: "inherit",
            textRendering: "inherit",
            textTransform: "inherit",
            //---------继承父元素------|
            whiteSpace: "pre-wrap",
            wordBreak: "keep-all",
            overflowWrap: "break-word",
            position: "absolute",
            top: 0,
            left: 0,
            height: "100%",
            width: "100%",
            resize: "none",
            color: "inherit",
            overflow: "hidden",
            MozOsxFontSmoothing: "grayscale",
            WebkitFontSmoothing: "antialiased",
            WebkitTextFillColor: "transparent", //???
            paddingTop: padding,
            paddingRight: padding,
            paddingBottom: padding,
            paddingLeft: padding
          }}
        />
        <pre
          aria-hidden="true"
          id="editableDiv"
          value={code}
          style={{
            margin: 0,
            border: 0,
            background: "none",
            //---------继承父元素------
            boxSizing: "inherit",
            display: "inherit",
            fontFamily: "inherit",
            fontSize: "inherit",
            fontStyle: "inherit",
            fontVariantLigatures: "inherit",
            fontWeight: "inherit",
            letterSpacing: "inherit",
            lineHeight: "inherit",
            tabSize: "inherit",
            textIndent: "inherit",
            textRendering: "inherit",
            textTransform: "inherit",
            //---------继承父元素------
            whiteSpace: "pre-wrap", //连续的空白符会被保留。在遇到换行符或者<br>元素，或者需要为了填充「行框盒子(line boxes)」时才会换行。
            wordBreak: "keep-all",
            overflowWrap: "break-word",
            position: "relative",
            pointerEvents: "none",
            paddingTop: padding,
            paddingRight: padding,
            paddingBottom: padding,
            paddingLeft: padding
          }}
        >
          <code className="language-js">{code}</code>
        </pre>
      </div>
    </div>
  );
}

export default CodeEditor;
