import React from "react";
import { Link } from "react-router-dom";
import "./StickyBlockBox.css";

function StickyBlockBox(props) {
  const { list } = props;
  return (
    <div className="stickyblockbox-wrap">
      {list.map((item, index) => (
        <div className="item" key={index + item}>
          <Link to={item.href} className="item-wrap">
            {/* <div> */}
            {item.type === "H1" && <span className="font-h1">{item.name}</span>}
            {item.type === "H2" && <span className="font-h2">{item.name}</span>}
            {/* </div> */}
          </Link>
        </div>
      ))}
    </div>
  );
}
export default StickyBlockBox;
