import React from "react";
import {
  BrowserRouter as Router,
  Route,
  useHistory,
  Link
  // withRouter
  // useLocation
} from "react-router-dom";
import PropTypes from "prop-types";
import List from "./list";

/**
 * @param {goBack} 返回
 * @param {goForward} 前进
 * @param {titleObject} 图标标题
 * @param {onClickTitle} 点击事件
 * @returns
 */

import Breadcrumb from "./view";

function BreadcrumbWrap(props) {
  let history = useHistory();
  // let location = useLocation();
  const { titleObject } = props;

  const goBack = () => {
    history.goBack();
  };
  const goForward = () => {
    history.goForward();
  };

  const handleClick = () => {
    history.push("/li");
  };

  return (
    <div>
      <Router>
        <Breadcrumb
          goback={goBack}
          goforword={goForward}
          titleObject={titleObject} //当前渲染页面记录 (包括icon titie)
          onClickTitle={handleClick} //跳转页面点击事件
        />
        <Link to="/">home </Link>
        <Link to="/li">list1 </Link>
        <Link to="/li2">list2 </Link>
        <Route>
          <List />
        </Route>
      </Router>
    </div>
  );
}
BreadcrumbWrap.protoTypes = {
  titleObject: PropTypes.object,
  onClickTitle: PropTypes.func
};
BreadcrumbWrap.defaultProps = {
  titleObject: [
    {
      icon: "🍉",
      title: "IT技术"
    },
    {
      icon: "🍉",
      title: "React"
    },

    {
      icon: "🍉",
      title: "123"
    }
  ],
  onClickTitle: () => {
    console.log("clickTitle");
  }
};
export default BreadcrumbWrap;
