import React, { useState, useEffect } from "react";
import Prism from "prismjs";
import { highlight, languages } from "prismjs/components/prism-core";
// import "../CodeTheme/Okadia.css";
import "./styles.css";

function CodeEditor() {
  const [code, setCode] = useState("");
  const padding = 20;
  const handleChange = e => {
    console.log(e);
    setCode(`${e.target.value}`);
  };

  useEffect(() => {
    Prism.highlightAll();
  });

  const highlighted = code => {
    highlight(code, languages.jsx);
  };
  return (
    <div>
      <div
        className="container__content"
        style={{
          position: "relative",
          textAlign: "left",
          boxSizing: "border-box",
          padding: 0,
          overflow: "hidden",
          fontSize: 18,
          height: 500,
          backgroundColor: "rgb(17, 49, 83)"
        }}
      >
        {/* -----------------代码input区------------------ */}

        <textarea
          value={code}
          type="text"
          onChange={handleChange}
          style={{
            margin: 0,
            border: 0,
            background: "none",
            //---------继承父元素------|
            boxSizing: "inherit",
            display: "inherit",
            fontFamily: "inherit",
            fontSize: "inherit",
            fontStyle: "inherit",
            fontVariantLigatures: "inherit",
            fontWeight: "inherit",
            letterSpacing: "inherit",
            lineHeight: "inherit",
            tabSize: "inherit",
            textIndent: "inherit",
            textRendering: "inherit",
            textTransform: "inherit",
            //---------继承父元素------|
            whiteSpace: "pre-wrap",
            wordBreak: "keep-all",
            overflowWrap: "break-word",
            position: "absolute",
            top: 0,
            left: 0,
            height: "100%",
            width: "100%",
            resize: "none",
            color: "inherit",
            overflow: "hidden",
            MozOsxFontSmoothing: "grayscale",
            WebkitFontSmoothing: "antialiased",
            WebkitTextFillColor: "transparent", //???
            paddingTop: padding,
            paddingRight: padding,
            paddingBottom: padding,
            paddingLeft: padding
          }}
        />
        <pre
          aria-hidden="true"
          id="editableDiv"
          value={code}
          style={{
            margin: 0,
            border: 0,
            background: "none",
            //---------继承父元素------
            boxSizing: "inherit",
            display: "inherit",
            fontFamily: "inherit",
            fontSize: "inherit",
            fontStyle: "inherit",
            fontVariantLigatures: "inherit",
            fontWeight: "inherit",
            letterSpacing: "inherit",
            lineHeight: "inherit",
            tabSize: "inherit",
            textIndent: "inherit",
            textRendering: "inherit",
            textTransform: "inherit",
            //---------继承父元素------
            whiteSpace: "pre-wrap",
            wordBreak: "keep-all",
            overflowWrap: "break-word",
            position: "relative",
            pointerEvents: "none",
            // top: 0,
            // left: 0,
            paddingTop: padding,
            paddingRight: padding,
            paddingBottom: padding,
            paddingLeft: padding
          }}
        >
          <code className="language-js">{code}</code>
        </pre>
      </div>
    </div>
  );
}

export default CodeEditor;
