import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  useParams,
  useLocation
} from "react-router-dom";

function BlogPost() {
  let { slug } = useParams();
  let location = useLocation();
  console.log(location);
  return <div>{slug}</div>;
}
function router() {
  return (
    <Router>
      <div>
        <Switch>
          <Route path="/blog/:slug">
            <BlogPost />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}
export default router;
