import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  useParams,
  useLocation,
  useHistory
} from "react-router-dom";

function BlogPost() {
  let { slug } = useParams();
  let location = useLocation();
  console.log(location);
  return <div>{slug}</div>;
}
function BackButton() {
  let history = useHistory();
  return (
    <button type="button" onClick={() => history.goBack()}>
      2222
    </button>
  );
}
function router() {
  // let history = useHistory();
  return (
    <Router>
      <div>
        <Switch>
          <button onClick={BackButton}>22</button>
          <Route path="/blog/:slug">
            <BlogPost />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}
export default router;
