import React from "react";
import { Editor, EditorState, RichUtils } from "draft-js";
class DraftEditor extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      editorState: EditorState.createEmpty()
    };
    this.onChange = editorState => {
      this.setState({ editorState });
    };
  }

  render() {
    const defaultInlineStyle = [
      { el: <span style={{ fontWeight: "bold" }}>B</span>, style: "BOLD" },
      { el: <span style={{ fontStyle: "italic" }}>I</span>, style: "ITALIC" },
      {
        el: <span style={{ textDecoration: "underline" }}>U</span>,
        style: "UNDERLINE"
      },
      {
        el: (
          <span>
            <div
              className="color-show"
              style={{ backgroundColor: "#e24" }}
            ></div>
          </span>
        ),
        style: "RED"
      },
      {
        el: (
          <span>
            <div
              className="color-show"
              style={{ backgroundColor: "#39f" }}
            ></div>
          </span>
        ),
        style: "BLUE"
      },
      {
        el: (
          <span>
            <div
              className="color-show"
              style={{ backgroundColor: "#f93" }}
            ></div>
          </span>
        ),
        style: "ORANGE"
      },
      {
        el: (
          <span>
            <div
              className="color-show"
              style={{ backgroundColor: "#3a6" }}
            ></div>
          </span>
        ),
        style: "GREEN"
      }
    ];
    let customColorStyleMap = {
      RED: { color: "#e24" },
      BLUE: { color: "#39f" },
      ORANGE: { color: "#f93" },
      GREEN: { color: "#3a6" }
    };
    let editorState = this.state.editorState;
    return (
      <div>
        <Editor
          placeholder={"Write what you would say."}
          editorState={editorState}
          onChange={this.onChange}
        />
        <Editor
          // basic config
          placeholder={"Write what you would say."}
          editorState={editorState}
          // event
          onChange={this.onChange} // style
          customStyleMap={customStyleMap}
        />
      </div>
    );
  }
}

export default DraftEditor;
