import React, { useEffect } from "react";
import Prism from "prismjs";
import "./styles.css";

function App() {
  useEffect(() => {
    Prism.highlightAll();
  });
  return (
    <pre className="line-numbers">
      <code className="language-js">
        const foo = 'foo'; const bar = 'bar'; console.log(foo + bar);
      </code>
    </pre>
  );
}
export default App;
