import React from "react";
import uuid from "uuid";
import "./Breadcrumb.css";
import {
  BrowserRouter as Router,
  Route,
  useHistory,
  Link
  // withRouter
  // useLocation
} from "react-router-dom";
/**
 *   Èù¢ÂåÖÂ±ë
 *+----------------------------------------+
 *|   üêª ITÊäÄÊúØ / üçîReact / üçâReact-hook
 *+---------------------------------------+
 * @param {goBack} ËøîÂõû
 * @param {goForward} ÂâçËøõ
 * @param {titleObject} ÂõæÊ†áÊ†áÈ¢ò
 * @param {onClickTitle} ÁÇπÂáª‰∫ã‰ª∂
 * @param {backslash} Èó¥ÈöîÁ¨¶ "/"
 * @returns
 */

function Breadcrumb(props) {
  const { goback, goforword, titleObject, onClickTitle } = props;

  return (
    <div className="topbar">
      <div className="button-wrap">
        <div className="goback" onClick={goback}>
          ->
        </div>
        <div className="goforword" onClick={goforword}>
          ->
        </div>
      </div>
      <div className="breadcrumb-wrap">
        {titleObject.map((item, index) => (
          <div key={uuid()}>
            <span className="icon">{item.icon}</span>
            <span className="title" onClick={() => onClickTitle(item)}>
              {item.title}
            </span>
            {index !== titleObject.length - 1 && (
              <span className="backslash">/</span>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default withRouter(Breadcrumb);
