import React, { useState, useEffect } from "react";
import marked from "marked";
import highlight from "highlight.js";
import Prism from "prismjs";
import "../CodeTheme/Okadia.css";
// import "../CodeTheme/Solarized.css";

highlight.configure({
  tabReplace: "  ",
  classPrefix: "hljs-",
  languages: [
    "CSS",
    "HTML, XML",
    "JavaScript",
    "PHP",
    "Python",
    "Stylus",
    "TypeScript",
    "Markdown"
  ]
});
marked.setOptions({
  highlight(code) {
    return highlight.highlightAuto(code).value;
  }
});
function App() {
  const [dataType, setDataType] = useState("js");
  const [previewContent, setPreviewContent] = useState("");
  // const [newContent, setnewContent] = useState("");

  const handleChange = e => {
    console.log(e);
    setPreviewContent(`${e.target.value}`);
  };
  const newhandleChange = e => {
    let element = document.getElementById("editableDiv");
    // let x = document.getElementById("editableDiv").isContentEditable;
    console.log(element.innerHTML);
    console.log(e.target.innerText);
    // console.log(x);
    setPreviewContent(element.innerHTML);
  };
  const handleChangeTypeJS = () => {
    setDataType(`js`);
  };
  const handleChangeTypeCSS = () => {
    setDataType(`css`);
  };
  const handleChangeTypeC = () => {
    setDataType("c");
  };
  useEffect(() => {
    setTimeout(() => Prism.highlightAll(), 0);
  });
  const handleSelectChange = e => {
    setDataType(`${e.target.value}`);
  };

  return (
    <div style={{ display: "flex" }}>
      {/* -----------------代码input区------------------ */}
      <div>
        <div>
          <ul>
            <button onClick={handleChangeTypeJS}>JavaScript</button>
            <button onClick={handleChangeTypeCSS}>CSS</button>
            <button onClick={handleChangeTypeC}>C++</button>
            &nbsp; &ensp; &emsp; &ensp; &emsp; &nbsp; &ensp; &emsp;
            <select name="type" onChange={handleSelectChange}>
              <option value="js">JavaScript</option>
              <option value="css">CSS</option>
              <option value="c++">C++</option>
            </select>
          </ul>
        </div>
        <textarea
          value={previewContent}
          placeholder="输入代码..."
          type="text"
          onChange={handleChange}
          style={{
            width: "500px",
            height: "100%",
            backgroundColor: "#f8f8f2"
          }}
        />
        <div
          style={{
            width: "500px"
          }}
        >
          <div>正常结果区:</div>
          {previewContent}
        </div>
      </div>
      {/* -----------------代码view区------------------ */}
      <button onClick={newhandleChange}>click</button>
      <div>
        <ul>代码区:{dataType}</ul>
        {/* <div
          contentEditable="plaintext-only"
          id="editableDiv"
          //  onInput={newhandleChange}
          onChange={newhandleChange}
          value={previewContent}
          style={{
            width: 500,
            height: 200,
            borderColor: "pink"
          }}
        ></div> */}
        {/* <div style={{ width: 500, height: 400 }}>
          <pre
            // contentEditable="true"
            contentEditable="plaintext-only"
            id="editableDiv"
            onInput={newhandleChange}
            // onChange={newhandleChange}
            value={previewContent}
            style={{ width: 500, height: 400 }}
            // className={`language-${dataType}`}
          > */}
        {/* <div
              className={`language-${dataType}`}
              dangerouslySetInnerHTML={{ __html: previewContent }}
            ></div> */}
        {/* <div className={`language-${dataType}`}>{previewContent}</div> */}
        {/* {previewContent}
          </pre>
        </div> */}

        {/* ---------------- */}
        <div
          className="editor-main-a"
          // ref={node => (this.aceBox = node)}
          // style={{ height: state.aceBoxH }}
          // key="main"
        >
          {/* //---------------------------------------editor------------------- */}
          <div
            className="common-container editor-container"
            // onMouseOver={this.setCurrentIndex.bind(this, 1)}
            //   onScroll={this.containerScroll}
            // ref={node => (this.editContainer = node)}
          >
            <div
              contentEditable="plaintext-only"
              name="editor-wrapper"
              id="editor-wrapper"
              className="common-wrapper editor-wrapper"
              onInput={newhandleChange}
              // ref={node => (this.editWrap = node)}
            ></div>
          </div>
          {/* //---------------------------------------view------------------- */}
          <div
            className="common-container preview-container"
            // ref={node => (this.previewContainer = node)}
            // onMouseOver={this.setCurrentIndex.bind(this, 2)}
            // onScroll={this.containerScroll}
          >
            <div
              className="markdown-body common-wrapper preview-wrapper"
              // ref={node => (this.previewWrap = node)}
              dangerouslySetInnerHTML={{ __html: previewContent }}
            ></div>
          </div>
        </div>

        {/* ------------------------- */}
      </div>
    </div>
  );
}

export default App;
