import React from "react";
import uuid from "uuid";
import { Link, useHistory } from "react-router-dom";
import "./Breadcrumb.css";

/**
 *   面包屑
 *+-------------------------------------------+
 *| ◀️ ▶️  🐻 IT技术 / 🍔React / 🍉React-hook
 *+-------------------------------------------+
 * @param {titleList} 数据对象,包括 icon,title, url components
 * @param {onClickTitle} 点击事件
 * @returns
 */

function Breadcrumb(props) {
  var titleList = props.titleList,
      onClickTitle = props.onClickTitle;

  var history = useHistory();
  return React.createElement(
    "div",
    { className: "topbar" },
    React.createElement(
      "div",
      { className: "button-wrap" },
      React.createElement(
        "div",
        {
          className: "goback",
          onClick: function onClick() {
            history.goBack();
          }
        },
        React.createElement("img", {
          className: "arrow-icon",
          src: require("../../public/arrowforback-icon.png")
        })
      ),
      React.createElement(
        "div",
        {
          className: "goforword",
          onClick: function onClick() {
            history.goForward();
          }
        },
        React.createElement("img", {
          className: "arrow-icon",
          src: require("../../public/arrowforward-icon.png")
        })
      )
    ),
    React.createElement(
      "div",
      { className: "breadcrumb-wrap" },
      titleList.map(function (item, index) {
        return React.createElement(
          "div",
          { key: uuid() },
          React.createElement(
            Link,
            { to: item.url, className: "breadcrumb-Link" },
            React.createElement(
              "span",
              { className: "icon" },
              item.icon
            ),
            React.createElement(
              "span",
              { className: "title" },
              item.title
            )
          ),
          index !== titleList.length - 1 && React.createElement(
            "span",
            { className: "backslash" },
            "/"
          )
        );
      })
    )
  );
}

export default Breadcrumb;